import React,{ useState } from "react";
import "./SudokoSolver.css";

const SudokuSolver = () => {
  const [sudoku, setSudoku] = useState(
    Array(9).fill(Array(9).fill("0"))
  ); // Initialize grid with empty strings
  const [output, setOutput] = useState("");

  // Update cell value
  const handleChange = (row, col, value) => {
    const newSudoku = [...sudoku];
    const newRow = [...newSudoku[row]];
    newRow[col] = value ? parseInt(value) : ""; // Only store numbers or empty
    newSudoku[row] = newRow;
    setSudoku(newSudoku);
  };

  // Clear the grid
  const clearGrid = () => {
    setSudoku(Array(9).fill(Array(9).fill("")));
    setOutput("");
  };

  // Solve Sudoku
  const solveSudoku = async () => {
    setOutput("Solving...");
    try {
      const response = await fetch("http://localhost:8080/solve-sudoko", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ grid: sudoku }),
      });

      if (response.ok) {
        const { solvedGrid } = await response.json();
        setSudoku(solvedGrid);
        setOutput("Solved Successfully!");
      } else {
        setOutput("Failed to solve Sudoku. Check the input.");
      }q
    } catch (error) {
      console.error("Error:", error);
      setOutput("Error connecting to the server.");
    }
  };

  return (
    <div className="container">
      <h1>Sudoku Solver</h1>
      <div className="sudoku-grid">
        {sudoku.map((row, rowIndex) => (
          <div className="sudoku-row" key={rowIndex}>
            {row.map((cell, colIndex) => (
              <input
                key={`${rowIndex}-${colIndex}`}
                type="number"
                min="1"
                max="9"
                value={cell || ""}
                onChange={(e) =>
                  handleChange(rowIndex, colIndex, e.target.value)
                }
              />
            ))}
          </div>
        ))}
      </div>
      <div className="buttons">
        <button onClick={solveSudoku}>Solve</button>
        <button onClick={clearGrid}>Clear</button>
      </div>
      <div className="output">{output}</div>
    </div>
  );
};

export default SudokuSolver;
